import { useState } from 'react'
import { Layout, Menu, Row, Button, Modal, Card, Table, Checkbox } from 'antd'
import './LandingPage.css'
import { PlusOutlined } from '@ant-design/icons'
import GridLayout from 'react-grid-layout'
import Navbar from '../../Navbar/Navbar'

export const LandingPage = () => {
  const { Content, Footer } = Layout

  const [isModulesVisible, setisModulesVisible] = useState(false)

  const options = [
    { label: 'Check in/out', value: 'check' },
    { label: 'Claims', value: 'claims' },
    { label: 'Leaves', value: 'leaves' },
    { label: 'Payslips', value: 'payslips' }
  ]

  const layout = [
    { i: 'check', x: 0, y: 0, w: 11, h: 22, isResizable: false },
    { i: 'claim', x: 11, y: 0, w: 11, h: 22, isResizable: false },
    { i: 'leave', x: 0, y: 0, w: 11, h: 22, isResizable: false },
    { i: 'payslip', x: 11, y: 0, w: 11, h: 22, isResizable: false },
    { i: 'add', x: 0, y: 0, w: 11, h: 22, isResizable: false }
  ]

  const dataSource = [
    {
      key: '1',
      name: 'Mike',
      age: 32,
      address: '10 Downing Street'
    },
    {
      key: '2',
      name: 'John',
      age: 42,
      address: '10 Downing Street'
    },
    {
      key: '3',
      name: 'Bob',
      age: 42,
      address: '10 Ning Street'
    },
    {
      key: '4',
      name: 'Tan',
      age: 42,
      address: '10 Tan Street'
    }
  ]

  const columns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name'
    },
    {
      title: 'Age',
      dataIndex: 'age',
      key: 'age'
    },
    {
      title: 'Address',
      dataIndex: 'address',
      key: 'address'
    }
  ]

  const menu = (
    <Menu>
      <Menu.Item>Profile</Menu.Item>
      <Menu.Item>Log Out</Menu.Item>
    </Menu>
  )

  return (
    <Layout>
      <Navbar name='main' />
      <Content className='layout'>
        <GridLayout
          className="grid"
          layout={layout}
          cols={22}
          rowHeight={10}
          width={1500}
          isBounded={true}
        >
          <div className='card' key='check'>
            <Card title='Check In/Out'>
              <Table
                dataSource={dataSource}
                columns={columns}
                pagination={{ hideOnSinglePage: true, disabled: true }}
                scroll={{ y: 240 }}
              />
              <Button
                shape='circle'
                type='primary'
                className='button'
                icon={<PlusOutlined />}
              ></Button>
            </Card>
          </div>
          <div className='card' key='claim'>
            <Card title='Claims'>
              <Table
                dataSource={dataSource}
                columns={columns}
                pagination={{ hideOnSinglePage: true, disabled: true }}
                scroll={{ y: 240 }}
              />
              <Button
                shape='circle'
                type='primary'
                className='button'
                icon={<PlusOutlined />}
              ></Button>
            </Card>
          </div>
          <div className='card' key='leave'>
            <Card title='Leaves'>
              <Table
                dataSource={dataSource}
                columns={columns}
                pagination={{ hideOnSinglePage: true, disabled: true }}
                scroll={{ y: 240 }}
              />
              <Button
                shape='circle'
                type='primary'
                className='button'
                icon={<PlusOutlined />}
              ></Button>
            </Card>
          </div>
          <div className='card' key='payslip'>
            <Card title='Payslips'>
              <Table
                dataSource={dataSource}
                columns={columns}
                pagination={{ hideOnSinglePage: true, disabled: true }}
                scroll={{ y: 240 }}
              />
              <Button
                shape='circle'
                type='primary'
                className='button'
                icon={<PlusOutlined />}
              ></Button>
            </Card>
          </div>
          <div className='card' key='add'>
            <Card>
              <Row
                style={{
                  justifyContent: 'center',
                  marginTop: '100px',
                  fontSize: '35px'
                }}
              >
                <div>Add/Remove Modules</div>
              </Row>
              <Row
                style={{
                  justifyContent: 'center',
                  marginTop: '40px',
                  marginBottom: '150px'
                }}
              >
                <Button
                  size='large'
                  shape='circle'
                  type='primary'
                  icon={<PlusOutlined />}
                  onClick={() => {
                    setisModulesVisible(true)
                  }}
                ></Button>
                <Modal
                  title='Add/Remove Modules'
                  visible={isModulesVisible}
                  okText='Confirm'
                  onOk={() => {
                    setisModulesVisible(false)
                  }}
                  onCancel={() => {
                    setisModulesVisible(false)
                  }}
                >
                  <Checkbox.Group options={options} />
                </Modal>
              </Row>
            </Card>
          </div>
        </GridLayout>
      </Content>
    </Layout>
  )
}
export default LandingPage
