import { useState } from 'react'
import {
  Layout,
  Menu,
  Input,
  Row,
  Col,
  Button,
  Modal,
  Card,
  Table,
  Checkbox,
  Space
} from 'antd'
import './LandingPage.css'
import {
  BellOutlined,
  UserOutlined,
  LogoutOutlined,
  PlusOutlined
} from '@ant-design/icons'
import logo from '../../Assets/download.png'
import GridLayout from 'react-grid-layout'

export const LandingPage = () => {
  const { Header, Content, Footer } = Layout

  const [isModulesVisible, setisModulesVisible] = useState(false)

  const options = [
    { label: 'Check in/out', value: 'check' },
    { label: 'Claims', value: 'claims' },
    { label: 'Leaves', value: 'leaves' },
    { label: 'Payslips', value: 'payslips' }
  ]

  const layout = [
    { i: 'a', x: 1, y: 0, w: 10, h: 22, isResizable: false },
    { i: 'b', x: 1, y: 0, w: 10, h: 22, isResizable: false },
    { i: 'c', x: 1, y: 0, w: 10, h: 22, isResizable: false },
    { i: 'd', x: 1, y: 0, w: 10, h: 22, isResizable: false },
    { i: 'e', x: 1, y: 0, w: 10, h: 22, isResizable: false }
  ]

  const dataSource = [
    {
      key: '1',
      name: 'Mike',
      age: 32,
      address: '10 Downing Street'
    },
    {
      key: '2',
      name: 'John',
      age: 42,
      address: '10 Downing Street'
    },
    {
      key: '3',
      name: 'Bob',
      age: 42,
      address: '10 Ning Street'
    },
    {
      key: '4',
      name: 'Tan',
      age: 42,
      address: '10 Tan Street'
    }
  ]

  const columns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name'
    },
    {
      title: 'Age',
      dataIndex: 'age',
      key: 'age'
    },
    {
      title: 'Address',
      dataIndex: 'address',
      key: 'address'
    }
  ]

  const menu = (
    <Menu>
      <Menu.Item>Profile</Menu.Item>
      <Menu.Item>Log Out</Menu.Item>
    </Menu>
  )

  return (
    <Layout>
      <Header style={{ background: '#fff' }}>
        <img className='logo' src={logo} />
        <div style={{ fontWeight: 700 }}>WELCOME AH BOY</div>
      </Header>
      <Row style={{ background: '#001529' }}>
        <Col span={12}>
          <Menu
            theme='dark'
            style={{ marginTop: 10 }}
            mode='horizontal'
            defaultSelectedKeys={['1']}
          >
            <Menu.Item className='items'>
              <Link to='/'>Home</Link>
            </Menu.Item>
            <Menu.Item className='items'>Leaves</Menu.Item>
            <Menu.Item className='items'>Claims</Menu.Item>
            <Menu.Item className='items'>Payslips</Menu.Item>
          </Menu>
        </Col>
        <Col span={3}>
          <Button
            className='avatar'
            icon={<LogoutOutlined />}
            style={{ background: '#c4c4c4' }}
          >
            Log Out
          </Button>

          {/*<Dropdown overlay={menu} placement='bottomLeft'>
        <Avatar className='avatar' icon={<UserOutlined />} />
  </Dropdown>*/}
        </Col>
      </Row>
      <Content className='layout'>
        <GridLayout
          className='layout'
          containerPadding={[0, 0]}
          layout={layout}
          cols={23}
          rowHeight={10}
          width={1500}
        >
          <div className='card' key='a'>
            <Card title='Check In/Out'>
              <Table
                dataSource={dataSource}
                columns={columns}
                pagination={{ hideOnSinglePage: true, disabled: true }}
                scroll={{ y: 240 }}
              />
              <Button
                shape='circle'
                type='primary'
                className='button'
                icon={<PlusOutlined />}
              ></Button>
            </Card>
          </div>
          <div className='card' key='b'>
            <Card title='Claims'>
              <Table
                dataSource={dataSource}
                columns={columns}
                pagination={{ hideOnSinglePage: true, disabled: true }}
                scroll={{ y: 240 }}
              />
              <Button
                shape='circle'
                type='primary'
                className='button'
                icon={<PlusOutlined />}
              ></Button>
            </Card>
          </div>
          <div className='card' key='c'>
            <Card title='Leaves'>
              <Table
                dataSource={dataSource}
                columns={columns}
                pagination={{ hideOnSinglePage: true, disabled: true }}
                scroll={{ y: 240 }}
              />
              <Button
                shape='circle'
                type='primary'
                className='button'
                icon={<PlusOutlined />}
              ></Button>
            </Card>
          </div>
          <div className='card' key='d'>
            <Card title='Payslips'>
              <Table
                dataSource={dataSource}
                columns={columns}
                pagination={{ hideOnSinglePage: true, disabled: true }}
                scroll={{ y: 240 }}
              />
              <Button
                shape='circle'
                type='primary'
                className='button'
                icon={<PlusOutlined />}
              ></Button>
            </Card>
          </div>
          <div className='card' key='e'>
            <Card>
              <Row
                style={{
                  justifyContent: 'center',
                  marginTop: '100px',
                  fontSize: '35px'
                }}
              >
                <div>Add/Remove Modules</div>
              </Row>
              <Row
                style={{
                  justifyContent: 'center',
                  marginTop: '40px',
                  marginBottom: '150px'
                }}
              >
                <Button
                  size='large'
                  shape='circle'
                  type='primary'
                  icon={<PlusOutlined />}
                  onClick={() => {
                    setisModulesVisible(true)
                  }}
                ></Button>
                <Modal
                  title='Add/Remove Modules'
                  visible={isModulesVisible}
                  okText='Confirm'
                  onOk={() => {
                    setisModulesVisible(false)
                  }}
                  onCancel={() => {
                    setisModulesVisible(false)
                  }}
                >
                  <Checkbox.Group options={options} />
                </Modal>
              </Row>
            </Card>
          </div>
        </GridLayout>
      </Content>
    </Layout>
  )
}
export default LandingPage
