import { useState } from 'react'
import { Layout, Col, Row, Button, Modal, Card, Table, Checkbox } from 'antd'
import './LandingPage.css'
import { PlusOutlined } from '@ant-design/icons'
import GridLayout from 'react-grid-layout'
import Navbar from '../../Navbar/Navbar'
import ClaimModal from '../../Modals/ClaimModal/ClaimModal'
import LeaveModal from '../../Modals/LeaveModal/LeaveModal'
import Clock from '../../Extras/Clock/Clock'

export const LandingPage = () => {
  const { Content, Footer } = Layout

  const [isModulesVisible, setisModulesVisible] = useState(false)

  const [isClaimVisible, setisClaimVisible] = useState(false)

  const [isLeaveVisible, setisLeaveVisible] = useState(false)

  const [isCheckIn, setisCheckIn] = useState(false)
  const toggleCheck = () => setisCheckIn(value => !value)

  const options = [
    { label: 'Check in/out', value: 'check' },
    { label: 'Claims', value: 'claims' },
    { label: 'Leaves', value: 'leaves' },
    { label: 'Payslips', value: 'payslips' }
  ]

  const layout = [
    { i: 'check', x: 0, y: 0, w: 11, h: 22, isResizable: false },
    { i: 'claim', x: 11, y: 0, w: 11, h: 22, isResizable: false },
    { i: 'leave', x: 0, y: 0, w: 11, h: 22, isResizable: false },
    { i: 'payslip', x: 11, y: 0, w: 11, h: 22, isResizable: false },
    { i: 'add', x: 0, y: 0, w: 11, h: 22, isResizable: false }
  ]

  const dataSource = [
    {
      key: '1',
      name: 'Mike',
      age: 32,
      address: '10 Downing Street'
    },
    {
      key: '2',
      name: 'John',
      age: 42,
      address: '10 Downing Street'
    },
    {
      key: '3',
      name: 'Bob',
      age: 42,
      address: '10 Ning Street'
    },
    {
      key: '4',
      name: 'Tan',
      age: 42,
      address: '10 Tan Street'
    },
    {
      key: '1',
      name: 'Mike',
      age: 32,
      address: '10 Downing Street'
    },
    {
      key: '2',
      name: 'John',
      age: 42,
      address: '10 Downing Street'
    },
    {
      key: '3',
      name: 'Bob',
      age: 42,
      address: '10 Ning Street'
    },
    {
      key: '4',
      name: 'Tan',
      age: 42,
      address: '10 Tan Street'
    }
  ]

  const claimsColumns = [
    {
      title: 'Claims ID',
      dataIndex: 'id',
      key: 'id'
    },
    {
      title: 'Reference No.',
      dataIndex: 'refer',
      key: 'refer'
    },
    {
      title: 'Claims Type',
      dataIndex: 'type',
      key: 'type'
    },
    {
      title: 'Claims Amount',
      dataIndex: 'amount',
      key: 'amount'
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status'
    },
    {
      title: 'Submission Date',
      dataIndex: 'date',
      key: 'date'
    }
  ]

  const leaveColumns = [
    {
      title: 'Claims ID',
      dataIndex: 'id',
      key: 'id'
    },
    {
      title: 'Reference No.',
      dataIndex: 'refer',
      key: 'refer'
    },
    {
      title: 'Claims Type',
      dataIndex: 'type',
      key: 'type'
    },
    {
      title: 'Claims Amount',
      dataIndex: 'amount',
      key: 'amount'
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status'
    },
    {
      title: 'Submission Date',
      dataIndex: 'date',
      key: 'date'
    }
  ]

  const payColumns = [
    {
      title: 'PaySlip Reference',
      dataIndex: 'refer',
      key: 'refer'
    },
    {
      title: 'Year',
      dataIndex: 'year',
      key: 'year'
    },
    {
      title: 'Month',
      dataIndex: 'month',
      key: 'month'
    },
    {
      title: 'Date From',
      dataIndex: 'from',
      key: 'from'
    },
    {
      title: 'Date To',
      dataIndex: 'to',
      key: 'to'
    },
    {
      title: 'Action',
      dataIndex: '',
      key: 'x',
      render: () => <Button icon={<EyeOutlined />}></Button>
    }
  ]

  return (
    <Layout>
      <Navbar name='main' />
      <Content className='layout'>
        <GridLayout
          className='grid'
          layout={layout}
          cols={22}
          rowHeight={10}
          width={1500}
          isBounded={true}
        >
          <div className='card' key='check'>
            <Card style={{ height: 'inherit' }} title='Check In/Out'>
              <Row>
                <Col style={{ fontSize: 40 }}>Status:</Col>
                <Col style={{ fontSize: 40, marginLeft: 10, fontWeight: 700 }}>
                  {isCheckIn ? 'Checked-In' : 'Checked-Out'}
                </Col>
              </Row>
              <Row>
                <Col style={{ fontSize: 40 }}>Current Time: </Col>
                <Col>
                  <Clock />
                </Col>
              </Row>
              <Row style={{marginTop:10}}>
                <Button
                  type='primary'
                  className='button'
                  onClick={toggleCheck}
                  size="large"
                > Check In</Button>
              </Row>
            </Card>
          </div>
          <div className='card' key='claim'>
            <Card style={{ height: 'inherit' }} title='Claims'>
              <Table
                dataSource={dataSource}
                columns={claimsColumns}
                pagination={{ hideOnSinglePage: true, disabled: true }}
                scroll={{ y: 240 }}
              />
              <Button
                shape='circle'
                type='primary'
                className='button'
                icon={<PlusOutlined />}
                onClick={() => {
                  setisClaimVisible(true)
                }}
              ></Button>
              <ClaimModal
                isClaimVisible={isClaimVisible}
                setisClaimVisible={setisClaimVisible}
              />
            </Card>
          </div>
          <div className='card' key='leave'>
            <Card style={{ height: 'inherit' }} title='Leaves'>
              <Table
                dataSource={dataSource}
                columns={leaveColumns}
                pagination={{ hideOnSinglePage: true, disabled: true }}
                scroll={{ y: 240 }}
              />
              <Button
                shape='circle'
                type='primary'
                className='button'
                icon={<PlusOutlined />}
                onClick={() => {
                  setisLeaveVisible(true)
                }}
              ></Button>
              <LeaveModal
                isLeaveVisible={isLeaveVisible}
                setisLeaveVisible={setisLeaveVisible}
              />
            </Card>
          </div>
          <div className='card' key='payslip'>
            <Card style={{ height: 'inherit' }} title='Payslips'>
              <Table
                dataSource={dataSource}
                columns={payColumns}
                pagination={{ hideOnSinglePage: true, disabled: true }}
                scroll={{ y: 240 }}
              />
            </Card>
          </div>
          <div className='card' key='add'>
            <Card>
              <Row
                style={{
                  justifyContent: 'center',
                  marginTop: '100px',
                  fontSize: '35px'
                }}
              >
                <div>Add/Remove Modules</div>
              </Row>
              <Row
                style={{
                  justifyContent: 'center',
                  marginTop: '40px',
                  marginBottom: '150px'
                }}
              >
                <Button
                  size='large'
                  shape='circle'
                  type='primary'
                  icon={<PlusOutlined />}
                  onClick={() => {
                    setisModulesVisible(true)
                  }}
                ></Button>
                <Modal
                  title='Add/Remove Modules'
                  visible={isModulesVisible}
                  okText='Confirm'
                  onOk={() => {
                    setisModulesVisible(false)
                  }}
                  onCancel={() => {
                    setisModulesVisible(false)
                  }}
                >
                  <Checkbox.Group options={options} />
                </Modal>
              </Row>
            </Card>
          </div>
        </GridLayout>
      </Content>
      <Footer>AMOGUS</Footer>
    </Layout>
  )
}
export default LandingPage
